/* eslint-disable react/jsx-pascal-case */
import React, {useState} from "react";

import './style.css';

import Movies from  './Dados/filmes';
import Filtro from './Filtros/index';
import Card_Movie from './Card/index';

function retornarInfoFilme(filmes, setFilme, top)
{
    let lista_filmes;

    if(top)
    {
        lista_filmes = filmes.slice(0,5).map(filme => 
            (
                <Card_Movie             
                title = {filme.title}
                backgroundImg = {filme.backgroundImg}
                starsCount = {filme.starsCount}
                price = {filme.price}
                categories = {filme.categories}
                isStarred = {filme.isStarred}
                AlterarEstadoFavorito = {AlterarEstadoFavorito}
                />
            )
        )
    }
    else
    {
        lista_filmes = filmes.map(filme => 
            (
                <Card_Movie             
                title = {filme.title}
                backgroundImg = {filme.backgroundImg}
                starsCount = {filme.starsCount}
                price = {filme.price}
                categories = {filme.categories}
                isStarred = {filme.isStarred}
                AlterarEstadoFavorito = {AlterarEstadoFavorito}
                />
            )
        )
    }
    return lista_filmes
}
/*
