{    
    const newFilmes = [...Filme];
    
    const Filme = newFilmes.find(filme => filme.title === tituloFilme);
    Filme.isStarred = !Filme.isStarred
    
    setFilme(newFilmes);
}
*/
export default function Filmes(props)
{    
    const [filme, setFilme] = useState(Movies);    
    
    return (
        <div className = "filmes">            
            <h3 className="txt-filmes">Top Filmes</h3>            
            <div className="top-conteudo">
                {retornarInfoFilme(filme, setFilme, "s")}
            </div>

            <h3 className="txt-filmes">Filmes</h3>
            <Filtro/>
            <div className="filmes-conteudo">
                {retornarInfoFilme(filme, setFilme)}
            </div>
        </div>
    )
}
