import './style.css';

export default function Filtros(props)
{
    const filtro_todos = props.filtroCategoria.length === 0 ? 'filtro_ativo' : '';
    const filtro_acao = props.filtroCategoria.includes('action') ? 'filtro_ativo' : '';
    const filtro_romance = props.filtroCategoria.includes('romance') ? 'filtro_ativo' : '';
    const filtro_ficcaoCientifica = props.filtroCategoria.includes('science fiction')? 'filtro_ativo' : '';
    const filtro_terro = props.filtroCategoria.includes('horror') ? 'filtro_ativo' : '';

    return (
        <div className = "filtros">
            <button className = {`filtro ${filtro_todos}`}>Todos</button>
            <button className = {`filtro ${filtro_todos}`}>Ação</button>
            <button className = {`filtro ${filtro_todos}`}>Romance</button>
            <button className = {`filtro ${filtro_todos}`}>Ficção Científica</button>
            <button className = {`filtro ${filtro_todos}`}>Terror</button>
        </div>
    )
}
