        <div className = "filtros">
            <button className = {`filtro `}>Todos</button>
            <button className = "filtro">Ação</button>
            <button className = "filtro">Romance</button>
            <button className = "filtro">Ficção Científica</button>
            <button className = "filtro">Terror</button>
        </div>
    )
}
