import './style.css';

import { ReactComponent as Logo} from '../../assets/images/logo.svg';
import { ReactComponent as IconePesquisa} from '../../assets/images/search-icon.svg';
import { ReactComponent as IconePromocao} from '../../assets/images/promotion-icon.svg';
import { ReactComponent as IconeFavoritos} from '../../assets/images/bookmark-icon.svg';
import { ReactComponent as IconeProvisorio} from '../../assets/images/minus-icon.svg';

function Header(props)
{
  return(
    <header className = "header">
      <Logo className="header_logo" />
  
      <div class="pesquisa">
          <input placeholder="Pesquise filmes..."></input>
          <IconePesquisa className="icone_pesquisar" />
      </div>

      <div class="favoritos">
          <IconeFavoritos className="icone_favoritos" />      
          <h5>Favoritos</h5>
      </div>






      <div class="favoritos">
          <img src = {iconeFavoritos} alt="Icone Favoritos"/>
          <h5>Favoritos</h5>
      </div>

      <div class="promocoes">
          <img src = {iconePromocao} alt="Icone Promocao"/>
          <h5>Promoções</h5>
      </div>

      <div class="bem-vindo">
          Bem vindo <span>Mayan</span>
          <img src = {iconeProvisorio}  alt="Imagem do Usuario"/>
      </div>   
    </header>     
  )
}

export default Header;


