import logo from './assets/images/logo.svg';
import iconePesquisa from './assets/images/search-icon.svg';
import favoritos from './assets/images/bookmark-icon.svg';

import './App.css';


function cabecalho()
{
  return(
    <div className="cabecalho">
      <img src = {logo} alt="Logomarca Empresa"/>

      <div class="pesquisa">
          <input placeholder="Pesquise filmes..."></input>
          <img src = {iconePesquisa} alt="Pesquisar"/>
      </div>

      <div class="favoritos">
          <img src="./assets/images/bookmark-icon.svg" alt=""/>
          <h5>Favoritos</h5>
      </div>

      <div class="promocoes">
          <img src="./assets/images/promotion-icon.svg" alt=""/>
          <h5>Promoções</h5>
      </div>

      <div class="bem-vindo">
          Bem vindo <span>Mayan</span>
          <img src="./assets/images/minus-icon.svg" alt=""/>
      </div>
    </div>
  )
}

function imagem()
{
  return <img src={logo} className="App-logo" alt="logo" />;
}

function App() {
  return (
    <div className="App">
      <header className="App-body">
        {cabecalho()}
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
